import { createRouter,createWebHistory} from "vue-router";
import Layout from "@/layout/HomeNav.vue";
import HomeLayout from "@/layout/HomeLayout";
import UserLayout from "@/layout/UserLayout";

const routes = [
    {
        path:'/',
        component : HomeLayout,
        redirect:'/home',
        children:[
            {
            path:'/home',
            component:() => import('@/views/home/HomePage.vue'),
            name:'home',
            meta:{
                title:'主页'
            }
        },
            {
                path:'/intro',
                component:() => import('@/views/home/IntroPage.vue'),
                name:'intro',
                meta:{
                    title:'介绍'
                }
            },
            {
                path:'/about',
                component:() => import('@/views/home/AboutPage.vue'),
                name:'about',
                meta:{
                    title:'关于'
                }
            },
            {
                path:'/support',
                component:() => import('@/views/home/supportPage.vue'),
                name:'support',
                meta:{
                    title:'支持'
                }
            },
        ]
    },

    {
        path:'/login',
        component : UserLayout,
        redirect:'/login',
        children:[{
            path:'/login',
            component:() => import('@/views/user/LoginUser.vue'),
            name:'/login',
            meta:{
                title:'登录'
            }
        },
            {
                path:'/register',
                component:() => import('@/views/user/RegisterUser.vue'),
                name:'/register',
                meta:{
                    title:'登录'
                }
            }
        ]
    },

    {
        path: '/init-voting',
        component: Layout,
        name:'init-voting',
        meta:{
            title: '发起投票',
        },
        children: [
            {
                path: '/avec',
                component:()=>import ('@/views/InitVote/avec/initAvec/AVEC.vue'),
                name: 'avec',
                meta: {
                    title: '匿名灵活投票'
                }
            },
            {
                path: '/freshavec',
                component:()=>import ('@/views/InitVote/avec/ReAVEC.vue'),
                name: 'freshavec',
                meta: {
                    title: '更新投票'
                }
            },
            {
                path: '/we',
                component:()=>import ('@/views/InitVote/we/WE.vue'),
                name: 'we',
                meta: {
                    title: '匿名权重投票'
                }
            },
            {
                path: '/freshwe',
                component:()=>import ('@/views/InitVote/we/ReWE.vue'),
                name: 'freshwe',
                meta: {
                    title: '更新投票'
                }
            },
        ]

    },
    {
        path:'/voting',
        component : Layout,
        redirect:'/voting',
        children:[{
            path:'/voting',
            component:() => import('@/views/voting/AddVoting.vue'),
            name:'voting',
            meta:{
                title:'投票'
            }
        }
        ]
    },
    {
        path:'/info',
        component : Layout,
        redirect:'/info',
        children:[
            {
            path:'/reslut',
            component:() => import('@/views/info/GetResult.vue'),
            name:'reslut',
            meta:{
                title:'投票结果'
            }
        },
            {
                path:'/voting-info',
                component:() => import('@/views/info/GetInfo.vue'),
                name:'voting-info',
                meta:{
                    title:'投票信息'
                }
            }
        ]
    },

]

const router = createRouter(
    {
        history:createWebHistory(),
        routes
    }
)
 export default router
